sys_script.script:
(function executeRule(current, previous /*null when async*/) {

    var shortDesc = current.short_description.getDisplayValue();

    if (shortDesc) {
        var predictor = new SmartPredictor(); 
        var prediction = predictor.predictAssignment(shortDesc);

        if (prediction) {
            current.assignment_group = prediction.group;
            current.assigned_to = prediction.user;
			current.x_215206_smrtnat_smart_assigned = true;


            var confidencePercent = (prediction.confidence * 100).toFixed(1);
            current.work_notes = 'Automatically assigned based on similarity to incident ' +
                                 prediction.matchIncident + ' (Confidence: ' + confidencePercent + '%).';

            gs.info('Incident ' + current.number + ' automatically assigned to ' + current.assignment_group.getDisplayValue());
			current.update();
        }
    }

})(current, previous);
