sys_script.script:
(function executeRule(current, previous /*null when async*/) {

    var FINGERPRINT_PROPERTY = 'x_215206_smrtnat.assignment_fingerprints';
    var MAX_FINGERPRINTS_PER_GROUP = 20; 

    try {
        var assignmentGroupSysId = current.assignment_group.getValue() ; 
        var shortDesc = current.short_description.getDisplayValue();

        var fingerprintProperty = gs.getProperty(FINGERPRINT_PROPERTY, '{}');
        var fingerprints = JSON.parse(fingerprintProperty);

        if (!fingerprints[assignmentGroupSysId]) {
            fingerprints[assignmentGroupSysId] = [];
        }

        var groupFingerprints = fingerprints[assignmentGroupSysId];

        groupFingerprints.unshift(shortDesc);

        var uniqueFingerprints = groupFingerprints.filter(function(item, pos) {
            return groupFingerprints.indexOf(item) == pos;
        });

        fingerprints[assignmentGroupSysId] = uniqueFingerprints.slice(0, MAX_FINGERPRINTS_PER_GROUP);

        var newFingerprintJSON = JSON.stringify(fingerprints);
        gs.setProperty(FINGERPRINT_PROPERTY, newFingerprintJSON);

		gs.log('Updated assignment fingerprints for group: ' + assignmentGroupSysId, 'Update Assignment Fingerprints');

    } catch (e) {
        gs.error('Error in "Update Assignment Fingerprints" business rule: ' + e.message, 'Update Assignment Fingerprints');
    }

})(current, previous);
